# <UDF name="UDFSERVERIPV6" label="Use IPV6?" default="Yes" oneof="Yes,No" />
# <UDF name="UDFSERVERFQDN" label="System Hostname -or- FQDN" default="" example="webserver -or- webserver.example.com" />

# Use Linode Staff's OP StackScript Function
source <ssinclude StackScriptID="1">

# Disable IPV6 (If User Requested)
[ $UDFSERVERIPV6 == "No"  ] && echo "net.ipv6.conf.eth0.disable_ipv6 = 1" | tee /etc/sysctl.d/70-disable-ipv6.conf && sysctl -p -f /etc/sysctl.d/70-disable-ipv6.conf

# Functions
function debian_autoremove {
  DEBIAN_FRONTEND=noninteractive apt-get autoremove -qq >/dev/null
}

# Package Manager Update
system_update
# Update Debian Packages
[ "${detected_distro[family]}" == 'debian' ] && DEBIAN_FRONTEND=noninteractive apt-get --with-new-pkgs upgrade -y && debian_autoremove
# Update CentOS Packages
[ "${detected_distro[distro]}" == 'centos' ] && yum --quiet -y install epel-release >/dev/null && yum update


# Validate User Hostname Entry (Regex: https://stackoverflow.com/a/20204811)
valid_fqdn=`echo $UDFSERVERFQDN | grep -P '(?=^.{1,253}$)(^(((?!-)[a-zA-Z0-9-]{1,63}(?<!-))|((?!-)[a-zA-Z0-9-]{1,63}(?<!-)\.)+[a-zA-Z]{2,63})$)'`

# Update Hostname and /etc/hosts
if ! [[ -z "$valid_fqdn" ]]; then
    # FQDN and Hostname
    if [[ "$valid_fqdn" == *"."* ]]; then
        echo "$valid_fqdn is fqdn"
        IFS='.' read -r -a array <<< "$valid_fqdn"
        valid_hostname=${array[0]}
        system_set_hostname "$valid_hostname"
        system_add_host_entry $(system_primary_ip) "$valid_fqdn" "$valid_hostname"
        # IPV6
        [ $UDFSERVERIPV6 == "Yes"  ] && system_add_host_entry $(system_primary_ipv6) "$valid_fqdn" "$valid_hostname"
    # Hostname Only
    else
        echo "$valid_fqdn is hostname"
        valid_hostname=$valid_fqdn
        system_set_hostname "$valid_hostname"
        system_add_host_entry $(system_primary_ip) "$valid_hostname"
        # IPV6
        [ $UDFSERVERIPV6 == "Yes"  ] && system_add_host_entry $(system_primary_ipv6) "$valid_hostname"
    fi
fi

# Set Timezone
system_set_timezone "$UDFSERVERTZ"
system_configure_ntp

# Basic Security
automatic_security_updates
ufw_install
enable_fail2ban